//Dom Variables

const largeImg = document.querySelector('.large-div-img');
console.log(largeImg);
const largeGreenDiv = document.getElementById('overlay-img');
const workDiv = document.getElementById('work-experience-title');
console.log(workDiv);
var e = workDiv.scrollHeight;
console.log(e);
const skills = document.querySelector('.skills-large');
console.log(skills);
const skillsOverlay = document.getElementById('skills-overlay');
const jobDuties = document.querySelector('.job-duties-large');
console.log(jobDuties);
const overlayJD = document.getElementById('overlay-duties');
console.log(overlayJD);
//add scroll listener to activate the slideInLeft animation
window.addEventListener('scroll', function(){
    if(window.scrollY >1400) {
       console.log('You have reached the bottom');
        largeGreenDiv.style.display = 'block';
        largeGreenDiv.style.animationDelay = '.2s';
        largeImg.style.opacity = 1;
    }
    
});

window.addEventListener('scroll', function() {
    if(window.scrollY > 2000){
     overlayJD.style.display ='block';
     overlayJD.style.transitionDelay = '3s';
     jobDuties.style.opacity = 1;
     skills.style.opacity =1;
     skillsOverlay.style.display = 'block';
    }
});



